def rounds(request:HttpResponse)-> HttpResponse:
    html_code:str = """
    <style>
    img.rot {
  animation: 1s linear 0s normal none infinite running rot;
  -webkit-animation: 1s linear 0s normal none infinite running rot;
  width: 100px;
}
@keyframes rot {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
@-webkit-keyframes rot {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}</style>
    <img class='rot' src='https://cdn.iconscout.com/icon/free/png-256/grinning-face-smile-emoji-happy-37705.png'>"""
    response: HttpResponse = HttpResponse(html_code)
    return response


def render_test(request:HttpResponse)-> HttpResponse:
    template_name: str = 'games/index.html'
    return render(
        request=request,
        template_name=template_name,
        context={}
    )

